import axios from "axios";

export function createUser(data, history) {
  console.log("create user");
  return function(dispatch) {
    axios.post("http://localhost:3001/createUser", data).then(response => {
      console.log(response);
      if (response.status === 200) {
        history.push("/SignIn");
      }
      dispatch({
        type: "CREATE_USERS",
        users: {}
      });
    });
  };
}

export function userLogin(data, history) {
  console.log("user login");
  return function(dispatch) {
    axios.defaults.withCredentials = true;
    axios.post("http://localhost:3001/loginUser", data).then(response => {
      if (response.status === 200) {
        history.push("/");
        dispatch({
          type: "USER_LOGIN",
          authflag: true
        });
      } else {
        history.push("/SignIn");
        dispatch({
          type: "USER_LOGIN",
          authflag: false
        });
      }
    });
  };
}
