import React, { Component } from "react";
import { connect } from "react-redux";
import * as actionCreators from "../../js/actions/ownerActions";
import { Row, Col } from "react-bootstrap";

class Cart extends Component {
  constructor(props) {
    super();
    this.state = {
      items: []
    };
  }

  componentWillMount() {
    this.props.getCartItems(this.props.user.id);
  }
  componentWillReceiveProps(nextProps) {
    const newUser = nextProps.user;
    const newcartItems = nextProps.cartItems;
    if (newUser !== this.props.user) {
      console.log(newUser);
      this.props.getCartItems(newUser.id);
    }
    if (newcartItems !== this.props.cartItems) {
      this.setState({
        items: newcartItems
      });
    }
  }
  render() {
    let cartItemsDisplay = this.state.items.map(item => {
      return (
        <div>
          <div class="row">
            <div className="s-col-xs-1 orderItem-quantity">{item.quantity}</div>
            <div
              className="s-col-xs-6 orderItem-quantity"
              style={{
                color: "blue"
              }}
            >
              {item.itemName}
            </div>
            <div
              className="s-col-xs-2 u-text-right orderItem-quantity"
              style={{
                cursor: "pointer"
              }}
            >
              <i class="trash icon"></i>
            </div>
            <div className="s-col-xs-2 orderItem-quantity">${item.price}</div>
          </div>
          <hr></hr>
        </div>
      );
    });
    return (
      <div>
        <hr></hr>
        {cartItemsDisplay}
      </div>
    );
  }
}
function mapStoreToProps(state) {
  return {
    user: state.userDetails.userProfile,
    cartItems: state.ownerDetails.cartItems
  };
}
export default connect(
  mapStoreToProps,
  actionCreators
)(Cart);
