import React, { Component } from "react";
import Navigation from "../Navbar";
import { connect } from "react-redux";
import * as actionCreators from "../../js/actions/index";
import Cart from "./Cart";
import {
  Container,
  Card,
  Col,
  Row,
  Modal,
  Button,
  Image,
  InputGroup,
  FormControl,
  Form
} from "react-bootstrap";
import "../../css/grubhub.css";
import { rooturl } from "../../config/constants";

class Message extends Component {
  constructor(props) {
    super();
    this.state = {
      restaurant: {},
      message: "",
      userId: ""
    };
  }
  componentWillMount() {
    if (!localStorage.getItem("jwtToken")) {
      localStorage.removeItem("email");
      localStorage.removeItem("password");
      this.props.history.push("/SignIn");
    }
    console.log(this.props.location.state);
    if (this.props.location.state) {
      this.setState({
        restaurant: this.props.location.state.restaurant,
        userId: this.props.location.state.userId
      });
      this.props.getMessages(this.props.location.state.userId);
    }
    console.log(this.state);
  }

  componentWillReceiveProps(nextProps) {
    newUser = nextProps.user;
    if (newUser !== this.props.user && newUser !== undefined) {
      this.props.getMessages(newUser._id);
    }
  }
  handleChange = event => {
    this.setState({
      message: event.target.value
    });
  };

  onSend = event => {
    if (this.state.message.trim().length > 0) {
      let data = {
        recieverId: this.state.restaurant._id,
        senderId: this.state.userId,
        message: this.state.message
      };
      console.log(data);
      this.props.sendMessages(data);
    }
  };

  render() {
    let messageList = this.props.message.map(message => {
      return (
        <div>
          {message.recieverId}
          <hr />
        </div>
      );
    });
    return (
      <div>
        <Navigation></Navigation>
        <Row>
          <Col className="col-sm-3">
            <div className="vertical-menu bg-light">
              <a href="/user/PastOrders" className="header">
                <h2>Messages</h2>
              </a>
              <a href="/UserHome">Home</a>
              <a href="/user/upcomingOrders">Upcoming orders</a>
              <a href="/Profile">Profile</a>
              <a href="/user/AddressAndPhone">Address and phone</a>
              <a href="/user/PastOrders">Past orders</a>
            </div>
          </Col>
          <Col className="col-sm-9">
            <Row>
              <Col className="col-sm-4">
                <Container>
                  <div className="vertical-menu">
                    <a href="/user/PastOrders" className="header">
                      <h2>Message List</h2>
                    </a>
                    <div>
                      {this.props.message.length > 0 ? (
                        <div></div>
                      ) : (
                        <div>
                          {this.state.restaurant ? (
                            <div>{this.state.restaurant.name}</div>
                          ) : (
                            <div>No messages</div>
                          )}
                        </div>
                      )}
                    </div>
                  </div>
                </Container>
              </Col>
              <div
                style={{
                  borderLeft: "4px solid black"
                }}
              ></div>
              <Col className="col-sm-7">
                <Container>
                  <div className="vertical-menu">
                    {this.props.message.length > 0 ? (
                      <div></div>
                    ) : (
                      <div>
                        {this.state.restaurant.name ? (
                          <div>
                            <h2>{this.state.restaurant.name}</h2>
                            <hr />
                            <div
                              style={{
                                minHeight: "600 px",
                                width: "100%",
                                border: "1px black"
                              }}
                            >
                              No Previous Messages
                            </div>
                            <hr />
                            <Form>
                              <Row>
                                <Col className="col-sm-10">
                                  <Form.Control
                                    placeholder="type message...."
                                    name="message"
                                    onChange={this.handleChange}
                                  ></Form.Control>
                                </Col>
                                <Col className="col-sm-2">
                                  <Button onClick={this.onSend}>Send</Button>
                                </Col>
                              </Row>
                            </Form>
                          </div>
                        ) : (
                          <div>No messages</div>
                        )}
                      </div>
                    )}
                  </div>
                </Container>
              </Col>
            </Row>
          </Col>
        </Row>
      </div>
    );
  }
}
function mapStoreToProps(state) {
  return {
    message: state.userDetails.messages,
    user: state.userDetails.userProfile
  };
}
export default connect(
  mapStoreToProps,
  actionCreators
)(Message);
