import React, { Component } from "react";
import Navigation from "../Navbar";
import { connect } from "react-redux";
import * as actionCreators from "../../js/actions/ownerActions";
import {
  Row,
  Col,
  Container,
  Button,
  Toast,
  Card,
  Modal,
  Alert
} from "react-bootstrap";
import "../../css/sidebar.css";
import Form from "react-bootstrap/Form";
import cookie from "react-cookies";

class OwnerHome extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [],
      showModal: false,
      orderDetails: null,
      updateStatus: false,
      status: "",
      alert: false
    };
  }

  componentWillMount() {
    if (!cookie.load("cookie")) {
      localStorage.removeItem("email");
      localStorage.removeItem("password");
      this.props.history.push("/SignIn");
    }
    this.props.getRestaurantDetails(localStorage.getItem("email"));
  }
  componentWillReceiveProps(nextProps) {
    const newRestaurantDetails = nextProps.restaurant;
    const newOrderItems = nextProps.getItems;
    if (newRestaurantDetails !== this.props.restaurant) {
      this.props.getOrdersforRestaurant(newRestaurantDetails.id);
    }
    if (newOrderItems !== this.props.getItems) {
      console.log("here");
      this.setState({
        items: newOrderItems
      });
    }
  }

  convertTodate(date) {
    date = date.split(".")[0];
    let dateTimeParts = date.split(/[-T :]/);
    dateTimeParts[1]--;
    console.log(dateTimeParts);
    return new Date(Date.UTC(...dateTimeParts));
  }
  getOrderDetails = id => event => {
    console.log(id);
    this.props.getOrdersItems(id);
    this.setState({
      showModal: true,
      orderDetails: this.props.getOrders.find(order => order.id === id)
    });
  };

  setUpdateStatus = () => {
    this.setState({
      updateStatus: true
    });
  };
  updateItemStatus = () => {
    if (this.state.status.length !== 0) {
      let data = {
        id: this.state.orderDetails.id,
        status: this.state.status
      };
      this.props.updateItemStatus(data, this.props.restaurant.id);
    }
    this.setState({
      updateStatus: false
    });
    this.setState(prevState => ({
      orderDetails: {
        ...prevState.orderDetails,
        status: this.state.status
      }
    }));
  };
  handleUpdateChange = event => {
    this.setState({
      status: event.target.value
    });
  };
  handleClose = event => {
    this.setState({
      showModal: false
    });
  };
  cancelOrder = () => {
    let data = {
      id: this.state.orderDetails.id,
      status: "Cancelled"
    };
    this.props.updateItemStatus(data, this.props.restaurant.id);
    this.setState({
      showModal: false,
      updateStatus: false,
      alert: false
    });
  };
  closeAlert = () => {
    this.setState({
      alert: false
    });
  };
  hancleCancleClick = () => {
    this.setState({
      alert: true
    });
  };
  render() {
    let newOrderDetails = this.props.getOrders.map(order => {
      if (order.status === "New") {
        return (
          <div
            as={Col}
            md={3}
            style={{
              margin: " 8px 0 0 8px",
              cursor: "pointer"
            }}
          >
            <Card onClick={this.getOrderDetails(order.id)}>
              <Card.Header>Order Id:{order.id}</Card.Header>
              <Card.Body>
                <Card.Title>New</Card.Title>
                <Card.Text>
                  Time:
                  {
                    order.createdAt
                      .toString()
                      .slice(0, 19)
                      .replace("T", " ")
                      .split(" ")[1]
                  }
                </Card.Text>
                <Card.Text>
                  Date:
                  {
                    order.createdAt
                      .toString()
                      .slice(0, 19)
                      .replace("T", " ")
                      .split(" ")[0]
                  }
                </Card.Text>
                <Card.Text>
                  {order.firstName} {order.lastName}
                </Card.Text>
              </Card.Body>
            </Card>
          </div>
        );
      }
    });
    let itemDetails = this.state.items.map(item => {
      return (
        <div>
          {item.itemName}({item.quantity}): {item.price}$
        </div>
      );
    });
    let prepOrderDetails = this.props.getOrders.map(order => {
      if (order.status === "Preparing" || order.status === "Ready") {
        return (
          <div
            as={Col}
            md={3}
            style={{
              margin: " 8px 0 0 8px",
              cursor: "pointer"
            }}
          >
            <Card onClick={this.getOrderDetails(order.id)}>
              <Card.Header>Order Id:{order.id}</Card.Header>
              <Card.Body>
                <Card.Title>{order.status}</Card.Title>
                <Card.Text>
                  Time:
                  {
                    order.createdAt
                      .toString()
                      .slice(0, 19)
                      .replace("T", " ")
                      .split(" ")[1]
                  }
                </Card.Text>
                <Card.Text>
                  Date:
                  {
                    order.createdAt
                      .toString()
                      .slice(0, 19)
                      .replace("T", " ")
                      .split(" ")[0]
                  }
                </Card.Text>
                <Card.Text>
                  {order.firstName} {order.lastName}
                </Card.Text>
              </Card.Body>
            </Card>
          </div>
        );
      }
    });
    return (
      <div>
        <Navigation></Navigation>
        <Row>
