import React, { Component } from "react";
import { connect } from "react-redux";
import { Navbar, Nav, NavDropdown } from "react-bootstrap";
import cookie from "react-cookies";
import { Redirect } from "react-router";
import * as actionCreators from "../js/actions/index";
class Navigation extends Component {
  constructor(props) {
    super(props);
    this.handleLogout = this.handleLogout.bind(this);
  }
  componentWillMount() {
    let data = {
      email: localStorage.getItem("email")
    };
    this.props.getUserProfile({});
  }
  componentWillMount;
  //handle logout to destroy the cookie
  handleLogout = () => {
    cookie.remove("cookie", { path: "/" });
  };
  render() {
    let navLogin = null;
    //let name = "Hi, " + this.props.user.getDetails.firstName + "!";
    if (cookie.load("cookie")) {
      console.log("cookie");
      console.log(this.props);
      navLogin = (
        <Nav className="ml-auto">
          <NavDropdown title="kavya" id="basic-nav-dropdown">
            <NavDropdown.Item href="#">
              <i class="clock icon"></i>Past Orders
            </NavDropdown.Item>
            <NavDropdown.Item href="#">
              <i class="calendar alternate icon"></i>Upcoming Orders
            </NavDropdown.Item>
            <NavDropdown.Item href="/Profile">
              <i class="cog icon"></i>Account Setting
            </NavDropdown.Item>
            <NavDropdown.Item href="#">
              <i class="info circle icon"></i>Help
            </NavDropdown.Item>
          </NavDropdown>
          <Nav.Link href="/" onClick={this.handleLogout}>
            <i class="sign out alternate icon"></i>Sign Out
          </Nav.Link>
        </Nav>
      );
    } else {
      console.log("No cookie");
      navLogin = (
        <Nav className="ml-auto">
          <Nav.Link href="/SignIn">
            <i class="sign in alternate icon"></i>Sign In
          </Nav.Link>
        </Nav>
      );
    }
    return (
      <Navbar bg="dark" variant="dark">
        <Navbar.Brand href="/">GrubHub</Navbar.Brand>
        <Navbar.Collapse>{navLogin}</Navbar.Collapse>
      </Navbar>
    );
  }
}
function mapStoreToProps(state) {
  return {
    user: state.userProfile
  };
}
export default connect(
  mapStoreToProps,
  actionCreators
)(Navigation);
