import React, { Component } from "react";
import Navigation from "../Navbar";
import { connect } from "react-redux";
import * as actionCreators from "../../js/actions/ownerActions";
import {
  Row,
  Col,
  Container,
  Button,
  Toast,
  Accordion,
  Card
} from "react-bootstrap";
import "../../css/sidebar.css";
import Form from "react-bootstrap/Form";
import cookie from "react-cookies";

class OwnerHome extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  componentWillMount() {
    if (!cookie.load("cookie")) {
      localStorage.removeItem("email");
      localStorage.removeItem("password");
      this.props.history.push("/SignIn");
    }
    this.props.getRestaurantDetails(localStorage.getItem("email"));
  }
  componentWillReceiveProps(nextProps) {
    const newRestaurantDetails = nextProps.restaurant;
    if (newRestaurantDetails !== this.props.restaurant) {
      this.props.getOrdersforRestaurant(newRestaurantDetails.id);
    }
  }

  render() {
    let newOrderDetails = this.props.getOrders.map(order => {
      if (order.status === "New") {
        return (
          <div>
            <Accordion defaultActiveKey="0">
              <Card>
                <Accordion.Toggle as={Card.Header} eventKey="0">
                  Click me!
                </Accordion.Toggle>
                <Accordion.Collapse eventKey="0">
                  <Card.Body>Hello! I'm the body</Card.Body>
                </Accordion.Collapse>
              </Card>
              <Card>
                <Accordion.Toggle as={Card.Header} eventKey="1">
                  Click me!
                </Accordion.Toggle>
                <Accordion.Collapse eventKey="1">
                  <Card.Body>Hello! I'm another body</Card.Body>
                </Accordion.Collapse>
              </Card>
            </Accordion>
          </div>
        );
      }
    });
    return (
      <div>
        <Navigation></Navigation>
        <Row>
          <Col className="col-sm-3">
            <div className="vertical-menu bg-light">
              <a className="header">
                <h2>Home</h2>
              </a>
              <a href="/OwnerHome" className="active">
                Home
              </a>
              <a href="/Menu">Menu</a>
              <a href="/OwnerProfile">Profile</a>
              <a href="#">Past Orders</a>
            </div>
          </Col>
          <Col className="col-sm-9">
            <Container>
              <div className="pt-3">
                <h1>Your Orders</h1>
              </div>
            </Container>
          </Col>
        </Row>
      </div>
    );
  }
}
function mapStoreToProps(state) {
  return {
    getOrders: state.ownerDetails.getOrdersRestaurant,
    restaurant: state.ownerDetails.restaurantDetails
  };
}
export default connect(
  mapStoreToProps,
  actionCreators
)(OwnerHome);
