import React, { Component } from "react";
import Navigation from "../Navbar";
import cookie from "react-cookies";
import { connect } from "react-redux";
import * as actionCreators from "../../js/actions/order";
import {
  Card,
  Container,
  InputGroup,
  FormControl,
  Button
} from "react-bootstrap";

class SearchPage extends Component {
  constructor(props) {
    super();
    this.state = {
      restaurantSearch: [],
      cuisine: ""
    };
  }
  componentWillMount() {
    if (!cookie.load("cookie")) {
      localStorage.removeItem("email");
      localStorage.removeItem("password");
      this.props.history.push("/SignIn");
    }
    console.log(this.props.location.state);
    if (this.props.location.state.menu.trim().length !== 0) {
      this.props.searchByMenu(this.props.location.state.menu.trim());
    }
  }
  componentWillReceiveProps(nextProps) {
    const newSearchMenu = nextProps.searchMenu;
    console.log(newSearchMenu);
    if (newSearchMenu !== this.props.searchMenu) {
      this.setState({
        restaurantSearch: newSearchMenu
      });
    }
  }
  handleCardClick = id => event => {
    console.log(id);
  };

  handleChange = event => {
    this.setState({ [event.target.name]: event.target.value });
  };
  filter = event => {
    this.setState(
      {
        restaurantSearch: []
      },
      () => {
        this.props.searchMenu.map(restaurant => {
          if (restaurant.cuisine.includes(this.state.cuisine)) {
            this.setState({
              restaurantSearch: this.state.restaurantSearch.concat(restaurant)
            });
          }
        });
      }
    );
  };
  render() {
    let searchByMenuList = this.state.restaurantSearch.map(restaurant => {
      return (
        <Card name={restaurant.id}>
          <a onClick={this.handleCardClick(restaurant.id)}>
            <Card.Body>
              <Card.Title>{restaurant.name}</Card.Title>
              <Card.Subtitle className="mb-2 text-muted">
                {restaurant.cuisine}
              </Card.Subtitle>
              <Card.Text>
                <i class="address card outline icon"></i>
                {restaurant.address}
              </Card.Text>
              <Card.Text>
                <i class="phone icon"></i>
                {restaurant.phoneNo}
              </Card.Text>
            </Card.Body>
          </a>
        </Card>
      );
    });
    return (
      <div>
        <Navigation></Navigation>
        <Container className="pt-5">
          <div>
            <h3>
              Best bets for{" "}
              <a
                style={{
                  color: "#0070eb"
                }}
              >
                {this.props.location.state.menu}
              </a>
            </h3>
            <div
              style={{
                fontSize: "12px",
                color: "#6b6b83"
              }}
            >
              {this.state.restaurantSearch.length || 0} Restaurants
            </div>
            <hr></hr>
          </div>
          <div className="d-flex justify-content-center">
            <InputGroup className="col-sm-6">
              <FormControl
                aria-label="Large"
                placeholder="Filter By Cuisine"
                name="cuisine"
                aria-describedby="inputGroup-sizing-sm"
                onChange={this.handleChange}
              ></FormControl>

              <InputGroup.Append>
                <Button variant="primary" onClick={this.filter}>
                  Filter
                </Button>
              </InputGroup.Append>
            </InputGroup>
          </div>
          <hr></hr>
          <div>{searchByMenuList}</div>
        </Container>
      </div>
    );
  }
}
function mapStoreToProps(state) {
  return {
    searchMenu: state.orderDetails.searchMenu
  };
}
export default connect(
  mapStoreToProps,
  actionCreators
)(SearchPage);
