import React, { Component } from "react";
import Navigation from "../Navbar";
import { connect } from "react-redux";
import { Row, Col, Container, Button, Table, Modal } from "react-bootstrap";
import "../../css/sidebar.css";
import Form from "react-bootstrap/Form";
import cookie from "react-cookies";
import * as actionCreators from "../../js/actions/ownerActions";

class Menu extends Component {
  constructor(props) {
    super();
    this.state = {
      isAddItem: false,
      itemName: "",
      price: "",
      section: "",
      desc: "",
      nameError: "",
      priceError: "",
      descError: "",
      sectionError: "",
      modal: {
        itemName: "",
        price: "",
        section: "",
        desc: "",
        nameError: "",
        priceError: "",
        descError: "",
        sectionError: "",
        id: ""
      },
      show: false
    };
  }
  async componentWillMount() {
    if (!cookie.load("cookie")) {
      localStorage.removeItem("email");
      localStorage.removeItem("password");
      this.props.history.push("/SignIn");
    }
    await this.props.getRestaurantDetails(localStorage.getItem("email"));
  }
  componentWillReceiveProps(nextProps) {
    const newRes = nextProps.restaurant;
    console.log("here");
    console.log(newRes);
    if (newRes !== this.props.restaurant) {
      this.props.getMenu(newRes[0].id);
    }
  }
  addItemFlag = () => {
    this.setState({
      isAddItem: true
    });
  };

  handleChange = event => {
    this.setState({ [event.target.name]: event.target.value });
  };

  validateAddItem = () => {
    this.setState({
      nameError: "",
      priceError: "",
      descError: "",
      sectionError: ""
    });
    let flag = true;
    if (this.state.itemName.trim().length === 0) {
      this.setState({
        nameError: "*Required"
      });
      flag = false;
    }
    if (this.state.desc.trim().length === 0) {
      this.setState({
        descError: "*Required"
      });
      flag = false;
    }
    if (this.state.price.trim().length === 0) {
      this.setState({
        priceError: "*Required"
      });
      flag = false;
    }
    if (this.state.section === "" || this.state.section === "--select--") {
      this.setState({
        sectionError: "*Required"
      });
      flag = false;
    }
    return flag;
  };
  addItem = () => {
    if (this.validateAddItem()) {
      let data = {
        name: this.state.itemName,
        desc: this.state.desc,
        price: this.state.price,
        section: this.state.section,
        restaurantId: this.props.restaurant[0].id
      };
      this.props.addMenuItem(data);
      window.location.reload();
    }
  };
  cancel = () => {
    this.setState({
      isAddItem: false
    });
  };
  update = event => {
    console.log(event.target.value);
    const item = this.props.menus.find(
      menu => menu.name === event.target.value
    );
    console.log(item);

    this.setState(prevState => ({
      modal: {
        ...prevState.modal,
        itemName: item.name,
        price: item.price,
        section: item.section,
        desc: item.description,
        id: item.id
      }
    }));
    this.setState({
      show: true
    });
  };
  handleClose = event => {
    this.setState({
      show: false
    });
  };
  handleModalNameChange = event => {
    let s = event.target.value;
    this.setState(prevState => ({
      modal: {
        ...prevState.modal,
        itemName: s
      }
    }));
  };
  handleModalDescChange = event => {
    let s = event.target.value;
    this.setState(prevState => ({
      modal: {
        ...prevState.modal,
        desc: s
      }
    }));
  };
  handleModalPriceChange = event => {
    let s = event.target.value;
    this.setState(prevState => ({
      modal: {
        ...prevState.modal,
        price: s
      }
    }));
  };
  handleModalSectionChange = event => {
    let s = event.target.value;
    this.setState(prevState => ({
      modal: {
        ...prevState.modal,
        section: s
      }
    }));
  };
  validateModalMenu = () => {
    this.setState(prevState => ({
      modal: {
        ...prevState.modal,
        nameError: "",
        priceError: "",
        descError: "",
        sectionError: ""
      }
    }));
    let flag = true;
    if (this.state.modal.itemName.trim().length === 0) {
      this.setState(prevState => ({
        modal: {
          ...prevState.modal,
          nameError: "*Required"
        }
      }));
      flag = false;
    }
    if (this.state.modal.desc.trim().length === 0) {
      this.setState(prevState => ({
        modal: {
          ...prevState.modal,
          descError: "*Required"
        }
      }));
      flag = false;
    }
    if (this.state.modal.price.trim().length === 0) {
      this.setState(prevState => ({
        modal: {
          ...prevState.modal,
          priceError: "*Required"
        }
      }));
      flag = false;
    }
    if (
      this.state.modal.section === "" ||
      this.state.modal.section === "--select--"
    ) {
      this.setState(prevState => ({
        modal: {
          ...prevState.modal,
          sectionError: "*Required"
        }
      }));
      flag = false;
    }
    return flag;
  };
  updateMenu = () => {
    if (this.validateModalMenu()) {
      console.log(this.state.modal);
      //this.props.updateMenuItem(this.state.modal);
      this.setState({
        show: false
      });
    }
  };
  render() {
    let menulist = this.props.menus.map(menu => (
      <tr>
        <td>{menu.name}</td>
        <td>{menu.description}</td>
        <td>{menu.image}</td>
        <td>{menu.price}</td>
        <td>{menu.section}</td>
        <td>
          <Button value={menu.name} onClick={this.update}>
            Update
          </Button>
        </td>
        <td>
          <Button value={menu} onClick={this.delete}>
            Delete
          </Button>
        </td>
      </tr>
    ));
    return (
      <div>
        <Navigation></Navigation>
        <div>
          <Row>
            <Col className="col-sm-3">
              <div className="vertical-menu bg-light">
                <a href="#" className="header">
                  <h2>Your Account</h2>
                </a>
                <a href="#">Home</a>
                <a href="#" className="active">
                  Menu
                </a>
                <a href="#">Profile</a>
                <a href="#">Past Orders</a>
              </div>
            </Col>
            <Col className="col-sm-9">
              <Container>
                <h3 className="pt-5">Manage Restaurant Menu</h3>
                <Button onClick={this.addItemFlag}>Add Item</Button>
                <div>
                  {this.state.isAddItem ? (
                    <div>
                      <Form>
                        <Row>
                          <Col>
                            <Form.Group>
                              <Form.Label>Item Name</Form.Label>
                              <Form.Control
                                name="itemName"
                                onChange={this.handleChange}
                              ></Form.Control>
                              <div
                                style={{ fontSize: 12, color: "red" }}
                                className="text-center"
                              >
                                {this.state.nameError}
                              </div>
                            </Form.Group>
                          </Col>
                          <Col>
                            <Form.Group>
                              <Form.Label>Item Description</Form.Label>
                              <Form.Control
                                name="desc"
                                onChange={this.handleChange}
                              ></Form.Control>
                              <div
                                style={{ fontSize: 12, color: "red" }}
                                className="text-center"
                              >
                                {this.state.descError}
                              </div>
                            </Form.Group>
                          </Col>

                          <Col>
                            <Form.Group>
                              <Form.Label>Price</Form.Label>
                              <Form.Control
                                name="price"
                                onChange={this.handleChange}
                              ></Form.Control>
                              <div
                                style={{ fontSize: 12, color: "red" }}
                                className="text-center"
                              >
                                {this.state.priceError}
                              </div>
                            </Form.Group>
                          </Col>
                          <Col>
                            <Form.Group>
                              <Form.Label>Section</Form.Label>
                              <Form.Control
                                as="select"
                                name="section"
                                onChange={this.handleChange}
                              >
                                <option defaultChecked>--select--</option>
                                <option>Breakfast</option>
                                <option>Lunch</option>
                                <option>Appetizers</option>
                              </Form.Control>
                              <div
                                style={{ fontSize: 12, color: "red" }}
                                className="text-center"
                              >
                                {this.state.sectionError}
                              </div>
                            </Form.Group>
                          </Col>
                        </Row>
                        <Button onClick={this.addItem}>ADD</Button>
                        <Button
                          variant="outline-secondary"
                          onClick={this.cancel}
                        >
                          Cancel
                        </Button>
                      </Form>
                    </div>
                  ) : (
                    <div></div>
                  )}
                  <Table striped bordered hover>
                    <thead>
                      <tr>
                        <th>Name</th>
                        <th>Description</th>
                        <th>Image</th>
                        <th>Price</th>
                        <th>Section</th>
                        <th></th>
                        <th></th>
                      </tr>
                    </thead>
                    <tbody>{menulist}</tbody>
                  </Table>
                  <Modal
                    show={this.state.show}
                    onHide={this.handleClose}
                    size="lg"
                    aria-labelledby="contained-modal-title-vcenter"
                    centered
                  >
                    <Modal.Header closeButton>
                      <Modal.Title>Update Item</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                      <Form>
                        <Row>
                          <Col>
                            <Form.Group>
                              <Form.Label>Item Name</Form.Label>
                              <Form.Control
                                name="itemName"
                                defaultValue={this.state.modal.itemName}
                                onChange={this.handleModalNameChange}
                              ></Form.Control>
                              <div
                                style={{ fontSize: 12, color: "red" }}
                                className="text-center"
                              >
                                {this.state.modal.nameError}
                              </div>
                            </Form.Group>
                          </Col>
                          <Col>
                            <Form.Group>
                              <Form.Label>Description</Form.Label>
                              <Form.Control
                                name="desc"
                                defaultValue={this.state.modal.desc}
                                onChange={this.handleModalDescChange}
                              ></Form.Control>
                              <div
                                style={{ fontSize: 12, color: "red" }}
                                className="text-center"
                              >
                                {this.state.modal.descError}
                              </div>
                            </Form.Group>
                          </Col>
                          <Col>
                            <Form.Group>
                              <Form.Label>Price</Form.Label>
                              <Form.Control
                                name="price"
                                defaultValue={this.state.modal.price}
                                onChange={this.handleModalPriceChange}
                              ></Form.Control>
                              <div
                                style={{ fontSize: 12, color: "red" }}
                                className="text-center"
                              >
                                {this.state.modal.priceError}
                              </div>
                            </Form.Group>
                          </Col>
                          <Col>
                            <Form.Group>
                              <Form.Label>Section</Form.Label>
                              <Form.Control
                                as="select"
                                name="section"
                                onChange={this.handleChange}
                                defaultValue={this.state.modal.section}
                                onChange={this.handleModalSectionChange}
                              >
                                <option defaultChecked>--select--</option>
                                <option>Breakfast</option>
                                <option>Lunch</option>
                                <option>Appetizers</option>
                              </Form.Control>
                              <div
                                style={{ fontSize: 12, color: "red" }}
                                className="text-center"
                              >
                                {this.state.modal.sectionError}
                              </div>
                            </Form.Group>
                          </Col>
                        </Row>
                      </Form>
                    </Modal.Body>
                    <Modal.Footer>
                      <Button variant="secondary" onClick={this.handleClose}>
                        Close
                      </Button>
                      <Button variant="primary" onClick={this.updateMenu}>
                        Update
                      </Button>
                    </Modal.Footer>
                  </Modal>
                </div>
              </Container>
            </Col>
          </Row>
        </div>
      </div>
    );
  }
}
function mapStoreToProps(state) {
  return {
    restaurant: state.ownerDetails.restaurantDetails,
    menus: state.ownerDetails.menu
  };
}
export default connect(
  mapStoreToProps,
  actionCreators
)(Menu);
