import React, { Component } from "react";
import Navigation from "../Navbar";
import { connect } from "react-redux";
import { Row, Col, Container, Button, Table, Modal } from "react-bootstrap";
import "../../css/sidebar.css";
import Form from "react-bootstrap/Form";
import cookie from "react-cookies";
import * as actionCreators from "../../js/actions/ownerActions";

class Menu extends Component {
  constructor(props) {
    super();
    this.state = {
      isAddItem: false,
      itemName: "",
      price: "",
      section: "",
      desc: "",
      nameError: "",
      priceError: "",
      descError: "",
      sectionError: "",
      modal: {
        show: false,
        itemName: "",
        price: "",
        section: "",
        desc: "",
        nameError: "",
        priceError: "",
        descError: "",
        sectionError: ""
      }
    };
  }
  async componentWillMount() {
    if (!cookie.load("cookie")) {
      localStorage.removeItem("email");
      localStorage.removeItem("password");
      this.props.history.push("/SignIn");
    }
    await this.props.getRestaurantDetails(localStorage.getItem("email"));
  }
  componentWillReceiveProps(nextProps) {
    const newRes = nextProps.restaurant;
    console.log("here");
    console.log(newRes);
    if (newRes !== this.props.restaurant) {
      this.props.getMenu(newRes[0].id);
    }
  }
  addItemFlag = () => {
    this.setState({
      isAddItem: true
    });
  };

  handleChange = event => {
    this.setState({ [event.target.name]: event.target.value });
  };

  validateAddItem = () => {
    this.setState({
      nameError: "",
      priceError: "",
      descError: "",
      sectionError: ""
    });
    let flag = true;
    if (this.state.itemName.trim().length === 0) {
      this.setState({
        nameError: "*Required"
      });
      flag = false;
    }
    if (this.state.desc.trim().length === 0) {
      this.setState({
        descError: "*Required"
      });
      flag = false;
    }
    if (this.state.price.trim().length === 0) {
      this.setState({
        priceError: "*Required"
      });
      flag = false;
    }
    if (this.state.section === "" || this.state.section === "--select--") {
      this.setState({
        sectionError: "*Required"
      });
      flag = false;
    }
    return flag;
  };
  addItem = () => {
    if (this.validateAddItem()) {
      let data = {
        name: this.state.itemName,
        desc: this.state.desc,
        price: this.state.price,
        section: this.state.section,
        restaurantId: this.props.restaurant[0].id
      };
      this.props.addMenuItem(data);
    }
  };
  cancel = () => {
    this.setState({
      isAddItem: false
    });
  };
  update = event => {
    console.log(event.target.value);
    const item = this.props.menus.find(
      menu => menu.name === event.target.value
    );
    console.log(item);

    this.setState({
      modal: {
        show: true,
        itemName: item.name,
        price: item.price,
        section: item.section,
        desc: item.description
      }
    });
  };
  handleClose = event => {
    this.setState({
      modal: { show: false }
    });
  };
  render() {
    let menulist = this.props.menus.map(menu => (
      <tr>
        <td>{menu.name}</td>
        <td>{menu.description}</td>
        <td>{menu.image}</td>
        <td>{menu.price}</td>
        <td>{menu.section}</td>
        <td>
          <Button value={menu.name} onClick={this.update}>
            Update
          </Button>
        </td>
        <td>
          <Button value={menu} onClick={this.delete}>
            Delete
          </Button>
        </td>
      </tr>
    ));
    return (
      <div>
        <Navigation></Navigation>
        <div>
          <Row>
            <Col className="col-sm-3">
              <div className="vertical-menu bg-light">
                <a href="#" className="header">
                  <h2>Your Account</h2>
                </a>
                <a href="#">Home</a>
                <a href="#" className="active">
                  Menu
                </a>
                <a href="#">Profile</a>
