import React, { Component } from "react";
import Navigation from "../Navbar";
import { connect } from "react-redux";
import * as actionCreators from "../../js/actions/index";
import { Row, Col, Container, Button } from "react-bootstrap";
import "../../css/sidebar.css";
import Form from "react-bootstrap/Form";

class UserProfile extends Component {
  constructor(props) {
    super();
    this.state = {
      isNameEditable: false,
      isEmailEditable: false,
      isPasswordEditable: false
    };
  }
  componentWillMount() {
    this.props.getUserProfile(localStorage.getItem("email"));
    console.log(this.props.profile);
  }
  handleNameEdit = event => {
    this.setState({ isNameEditable: true });
  };
  render() {
    let nameEdit = null;
    if (this.state.isNameEditable) {
      nameEdit = (
        <div>
          <h3>Edit Name</h3>
          <Form className="col-sm-5">
            <Form.Group>
              <Form.Label>First Name</Form.Label>
              <Form.Control name="firstName"></Form.Control>
            </Form.Group>
            <Form.Group>
              <Form.Label>Last Name</Form.Label>
              <Form.Control name="lastName"></Form.Control>
            </Form.Group>
            <Form.Group>
              <Button>Update Name</Button>
              <Button variant="outline-secondary">Cancel</Button>
            </Form.Group>
          </Form>
          <hr></hr>
        </div>
      );
    } else {
      nameEdit = (
        <div>
          <label className="pt-2">
            <b>Name</b>
          </label>
          <div>
            kavyashree chandrashekar
            <div
              class="float-right"
              role="button"
              style={{ color: "blue" }}
              onClick={this.handleNameEdit}
            >
              Edit
            </div>
          </div>
          <hr></hr>
        </div>
      );
    }
    let emailEdit = null;
    if (this.state.isEmailEditable) {
      emailEdit = (
        <div>
          <h3>Edit Email</h3>
          <Form className="col-sm-5">
            <Form.Group>
              <Form.Label>New Email</Form.Label>
              <Form.Control name="email"></Form.Control>
            </Form.Group>
            <Form.Group>
              <Form.Label>Confirm email</Form.Label>
              <Form.Control name="confirmEmail"></Form.Control>
            </Form.Group>
            <Form.Group>
              <Button>Update Name</Button>
              <Button variant="outline-secondary">Cancel</Button>
            </Form.Group>
          </Form>
          <hr></hr>
        </div>
      );
    } else {
      emailEdit = (
        <div>
          <label className="pt-2">
            <b>Email</b>
          </label>
          <div>
            something@gmail.com
            <div
              class="float-right"
              role="button"
              style={{ color: "blue" }}
              onClick={this.handleNameEdit}
            >
              Edit
            </div>
          </div>
          <hr></hr>
        </div>
      );
    }
    return (
      <div>
        <Navigation></Navigation>
        <div>
          <Row>
            <Col className="col-sm-3">
              <div className="vertical-menu bg-light">
                <a href="#" className="header">
                  <h2>Your Account</h2>
                </a>
                <a href="#" className="active">
                  Profile
                </a>
                <a href="#">Address and phone</a>
                <a href="#">Past orders</a>
                <a href="#">Upcoming orders</a>
              </div>
            </Col>
            <Col className="col-sm-9">
              <Container>
                <h3 className="pt-5">Your Account</h3>

                {nameEdit}
                {emailEdit}
              </Container>
            </Col>
          </Row>
        </div>
      </div>
    );
  }
}

function mapStoreToProps(state) {
  return {
    profile: state.userDetails
  };
}
export default connect(
  mapStoreToProps,
  actionCreators
)(UserProfile);
