import React, { Component } from "react";
import Navigation from "../Navbar";
import cookie from "react-cookies";
import { connect } from "react-redux";
import * as actionCreators from "../../js/actions/index";
import Cart from "./Cart";
import {
  Container,
  Card,
  Col,
  Row,
  Modal,
  Button,
  Image,
  InputGroup,
  FormControl
} from "react-bootstrap";
import image from "../../images/pizza.jpeg";
import "../../css/grubhub.css";
import { isNewExpression } from "@babel/types";

class PastOrdersPage extends Component {
  constructor(props) {
    super();
    this.state = {
      items: []
    };
  }

  async componentWillMount() {
    if (!cookie.load("cookie")) {
      localStorage.removeItem("email");
      localStorage.removeItem("password");
      this.props.history.push("/SignIn");
    }
    await this.props.getUserProfile(localStorage.getItem("email"));
    console.log(this.props.profile);
  }
  componentWillReceiveProps(nextProps) {
    const newProfile = nextProps.profile;
    const newItems = nextProps.itemDetails;
    if (newProfile !== this.props.profile) {
      this.props.getOrderDetails(newProfile.id);
      this.props.getItemDetails(newProfile.id);
    }
    if (newItems !== this.props.itemDetails) {
      this.setState({
        items: newItems
      });
    }
  }
  render() {
    let itemDetails = id => {
      let items = this.state.items.map(item => {
        if (item.id === id) {
          return <div>{item.itemName}</div>;
        }
      });
      console.log(items);
      return items;
    };
    let pastOrderDetails = this.props.orderDetails.map(order => {
      if (order.status) {
        return (
          <div
            style={{
              margin: " 8px 0 0 8px",
              cursor: "pointer"
            }}
          >
            <Card>
              <Card.Header>Order Id:{order.id}</Card.Header>
              <Card.Body as={Row}>
                <Card.Title>{order.status}</Card.Title>
                <Card.Text>
                  Time:
                  {
                    order.createdAt
                      .toString()
                      .slice(0, 19)
                      .replace("T", " ")
                      .split(" ")[1]
                  }
                </Card.Text>
                <Card.Text>
                  Date:
                  {
                    order.createdAt
                      .toString()
                      .slice(0, 19)
                      .replace("T", " ")
                      .split(" ")[0]
                  }
                </Card.Text>
                <Card.Text>{order.name}</Card.Text>
                <Card.Text>{itemDetails(order.id)}</Card.Text>
              </Card.Body>
            </Card>
          </div>
        );
      }
      return null;
    });
    return (
      <div>
        <Navigation></Navigation>
        <Row>
          <Col className="col-sm-3">
            <div className="vertical-menu bg-light">
              <a href="/Owner/PastOrders" className="header">
                <h2>Past Orders</h2>
              </a>
              <a href="/OwnerHome">Home</a>
              <a href="/Menu">Menu</a>
              <a href="/OwnerProfile">Profile</a>
              <a href="/Owner/PastOrders" className="active">
                Past Orders
              </a>
            </div>
          </Col>
          <Col className="col-sm-9">
            <Container>
              <div className="pt-3">
                <h1>Your Orders</h1>
              </div>
              <hr></hr>
              <div>
                <h3>Past Orders</h3>
              </div>
              <div>{pastOrderDetails}</div>
            </Container>
          </Col>
        </Row>
      </div>
    );
  }
}

function mapStoreToProps(state) {
  return {
    profile: state.userDetails.userProfile,
    orderDetails: state.userDetails.orderDetails,
    itemDetails: state.userDetails.itemDetails
  };
}
export default connect(
  mapStoreToProps,
  actionCreators
)(PastOrdersPage);
