import React, { Component } from "react";
import Navigation from "../Navbar";
import { connect } from "react-redux";
import { Row, Col, Container, Button, Table } from "react-bootstrap";
import "../../css/sidebar.css";
import Form from "react-bootstrap/Form";
import cookie from "react-cookies";
import * as actionCreators from "../../js/actions/ownerActions";

class Menu extends Component {
  constructor(props) {
    super();
    this.state = {
      isAddItem: false,
      itemName: "",
      price: "",
      section: "",
      desc: "",
      nameError: "",
      priceError: "",
      descError: "",
      sectionError: ""
    };
  }
  async componentWillMount() {
    if (!cookie.load("cookie")) {
      localStorage.removeItem("email");
      localStorage.removeItem("password");
      this.props.history.push("/SignIn");
    }
    await this.props.getRestaurantDetails(localStorage.getItem("email"));
  }
  componentWillReceiveProps(nextProps) {
    const newRes = nextProps.restaurant;
    console.log("here");
    console.log(newRes);
    if (newRes !== this.props.restaurant) {
      this.props.getMenu(newRes[0].id);
    }
  }
  addItemFlag = () => {
    this.setState({
      isAddItem: true
    });
  };

  handleChange = event => {
    this.setState({ [event.target.name]: event.target.value });
  };

  validateAddItem = () => {
    this.setState({
      nameError: "",
      priceError: "",
      descError: "",
      sectionError: ""
    });
    let flag = true;
    if (this.state.itemName.trim().length === 0) {
      this.setState({
        nameError: "*Required"
      });
      flag = false;
    }
    if (this.state.desc.trim().length === 0) {
      this.setState({
        descError: "*Required"
      });
      flag = false;
    }
    if (this.state.price.trim().length === 0) {
      this.setState({
        priceError: "*Required"
      });
      flag = false;
    }
    if (this.state.section === "" || this.state.section === "--select--") {
      this.setState({
        sectionError: "*Required"
      });
      flag = false;
    }
    return flag;
  };
  addItem = () => {
    if (this.validateAddItem()) {
      let data = {
        name: this.state.isAddItem,
        desc: this.state.desc,
        price: this.state.price,
        section: this.state.section
      };
    }
  };
  render() {
    return (
      <div>
        <Navigation></Navigation>
        <div>
          <Row>
            <Col className="col-sm-3">
              <div className="vertical-menu bg-light">
                <a href="#" className="header">
                  <h2>Your Account</h2>
                </a>
                <a href="#">Home</a>
                <a href="#" className="active">
                  Menu
                </a>
                <a href="#">Profile</a>
                <a href="#">Past Orders</a>
              </div>
            </Col>
            <Col className="col-sm-9">
              <Container>
                <h3 className="pt-5">Manage Restaurant Menu</h3>
                <Button onClick={this.addItemFlag}>Add Item</Button>
                <div>
                  {this.state.isAddItem ? (
                    <div>
                      <Form>
                        <Row>
                          <Col>
                            <Form.Group>
                              <Form.Label>Item Name</Form.Label>
                              <Form.Control
                                name="itemName"
                                onChange={this.handleChange}
                              ></Form.Control>
                            </Form.Group>
                          </Col>
                          <Col>
                            <Form.Group>
                              <Form.Label>Item Description</Form.Label>
                              <Form.Control
                                name="desc"
                                onChange={this.handleChange}
                              ></Form.Control>
                            </Form.Group>
                          </Col>

                          <Col>
                            <Form.Group>
                              <Form.Label>Price</Form.Label>
                              <Form.Control
                                name="price"
                                onChange={this.handleChange}
                              ></Form.Control>
                            </Form.Group>
                          </Col>
                          <Col>
                            <Form.Group>
                              <Form.Label>Section</Form.Label>
                              <Form.Control
                                as="select"
                                name="section"
                                onChange={this.handleChange}
                              >
                                <option defaultChecked>--select--</option>
                                <option>Breakfast</option>
                                <option>Lunch</option>
                                <option>Appetizers</option>
                              </Form.Control>
                            </Form.Group>
                          </Col>
                        </Row>
                        <Button>ADD</Button>
                        <Button variant="outline-secondary">Cancel</Button>
                      </Form>
                    </div>
                  ) : (
                    <div></div>
                  )}
                  <Table striped bordered hover>
                    <thead>
                      <tr>
                        <th>Name</th>
                        <th>Description</th>
                        <th>Image</th>
                        <th>Price</th>
                        <th>Section</th>
                      </tr>
                    </thead>
                  </Table>
                </div>
              </Container>
            </Col>
          </Row>
        </div>
      </div>
    );
  }
}
function mapStoreToProps(state) {
  return {
    restaurant: state.ownerDetails.restaurantDetails
  };
}
export default connect(
  mapStoreToProps,
  actionCreators
)(Menu);
