import axios from "axios";

export function createOwner(data, history) {
  console.log("create user");
  return function(dispatch) {
    axios.post("http://localhost:3001/createOwner", data).then(response => {
      console.log(response);
      if (response.status === 200) {
        history.push("/SignIn");
      }
      dispatch({
        type: "CREATE_OWNER",
        payload: {}
      });
    });
  };
}

export function getRestaurantDetails(data) {
  return function(dispatch) {
    axios.get("http://localhost:3001/restaurant/" + data).then(response => {
      if (response.status === 200) {
        dispatch({
          type: "RESTAURANT_DETAILS",
          payload: response.data
        });
      }
    });
  };
}

export function getMenu(data) {
  console.log("inside get menu");
  return function(dispatch) {
    axios.get("http://localhost:3001/menu/" + data).then(response => {
      if (response.status === 200) {
        dispatch({
          type: "MENU",
          payload: response.data
        });
      }
    });
  };
}

export function addMenuItem(data, id) {
  return function(dispatch) {
    axios.post("http://localhost:3001/createmenu", data).then(response => {
      if (response.status === 200) {
        dispatch(getMenu(id));
      } else {
        dispatch({
          type: "ADD_MENU",
          payload: "Unable to Update"
        });
      }
    });
  };
}

export function updateMenuItem(data, id) {
  return function(dispatch) {
    axios.put("http://localhost:3001/updatemenu", data).then(response => {
      if (response.status === 200) {
        dispatch(getMenu(id));
      } else {
        dispatch({
          type: "ADD_MENU",
          payload: "Unable to Update"
        });
      }
    });
  };
}
