import React, { Component } from "react";
import Navigation from "../Navbar";
import cookie from "react-cookies";
import { connect } from "react-redux";
import * as actionCreators from "../../js/actions/index";
import Cart from "./Cart";
import {
  Container,
  Card,
  Col,
  Row,
  Modal,
  Button,
  Image,
  InputGroup,
  FormControl
} from "react-bootstrap";
import image from "../../images/pizza.jpeg";
import "../../css/grubhub.css";

class PastOrdersPage extends Component {
  constructor(props) {
    super();
    this.state = {};
  }

  componentWillMount() {
    if (!cookie.load("cookie")) {
      localStorage.removeItem("email");
      localStorage.removeItem("password");
      this.props.history.push("/SignIn");
    }
    this.props.getOrderDetails(this.props.userProfile.id);
  }
  render() {
    let pastOrderDetails = this.props.getOrders.map(order => {
      if (order.status === "Delivered" || order.status === "Cancelled") {
        return (
          <div
            as={Col}
            md={3}
            style={{
              margin: " 8px 0 0 8px",
              cursor: "pointer"
            }}
          >
            <Card>
              <Card.Header>Order Id:{order.id}</Card.Header>
              <Card.Body>
                <Card.Title>{order.status}</Card.Title>
                <Card.Text>
                  Time:
                  {
                    order.createdAt
                      .toString()
                      .slice(0, 19)
                      .replace("T", " ")
                      .split(" ")[1]
                  }
                </Card.Text>
                <Card.Text>
                  Date:
                  {
                    order.createdAt
                      .toString()
                      .slice(0, 19)
                      .replace("T", " ")
                      .split(" ")[0]
                  }
                </Card.Text>
                <Card.Text>
                  {order.firstName} {order.lastName}
                </Card.Text>
              </Card.Body>
            </Card>
          </div>
        );
      }
      return null;
    });
    return (
      <div>
        <Navigation></Navigation>
        <Row>
          <Col className="col-sm-3">
            <div className="vertical-menu bg-light">
              <a href="/Owner/PastOrders" className="header">
                <h2>Past Orders</h2>
              </a>
              <a href="/OwnerHome">Home</a>
              <a href="/Menu">Menu</a>
              <a href="/OwnerProfile">Profile</a>
              <a href="/Owner/PastOrders" className="active">
                Past Orders
              </a>
            </div>
          </Col>
          <Col className="col-sm-9">
            <Container>
              <div className="pt-3">
                <h1>Your Orders</h1>
              </div>
              <hr></hr>
              <div>
                <h3>Past Orders</h3>
              </div>
              <Row>{pastOrderDetails}</Row>
            </Container>
          </Col>
        </Row>
      </div>
    );
  }
}

function mapStoreToProps(state) {
  return {
    userProfile: state.UserDetails.userProfile
  };
}
export default connect(
  mapStoreToProps,
  actionCreators
)(PastOrdersPage);
