import React, { Component } from "react";
import Navigation from "../Navbar";
import { connect } from "react-redux";
import { Row, Col, Container, Button, Toast } from "react-bootstrap";
import "../../css/sidebar.css";
import Form from "react-bootstrap/Form";
import cookie from "react-cookies";

class Menu extends Component {
  constructor(props) {
    super();
    this.state = {
      isAddItem: false
    };
  }
  componentWillMount() {
    if (!cookie.load("cookie")) {
      localStorage.removeItem("email");
      localStorage.removeItem("password");
      this.props.history.push("/SignIn");
    }
  }
  addItem = () => {
    this.setState({
      isAddItem: true
    });
  };
  render() {
    return (
      <div>
        <Navigation></Navigation>
        <div>
          <Row>
            <Col className="col-sm-3">
              <div className="vertical-menu bg-light">
                <a href="#" className="header">
                  <h2>Your Account</h2>
                </a>
                <a href="#">Home</a>
                <a href="#" className="active">
                  Menu
                </a>
                <a href="#">Profile</a>
                <a href="#">Past Orders</a>
              </div>
            </Col>
            <Col className="col-sm-9">
              <Container>
                <h3 className="pt-5">Manage Restaurant Menu</h3>
                <Button onClick={this.addItem}>Add Item</Button>
                <div>
                  {this.state.isAddItem ? (
                    <div>
                      <Form>
                        <Row>
                          <Col>
                            <Form.Group>
                              <Form.Label>Item Name</Form.Label>
                              <Form.Control name="itemName"></Form.Control>
                            </Form.Group>
                          </Col>
                          <Col>
                            <Form.Group>
                              <Form.Label>Item Description</Form.Label>
                              <Form.Control name="desc"></Form.Control>
                            </Form.Group>
                          </Col>
                        </Row>
                        <Row>
                          <Col>
                            <Form.Group>
                              <Form.Label>Price</Form.Label>
                              <Form.Control name="price"></Form.Control>
                            </Form.Group>
                          </Col>
                          <Col>
                            <Form.Group>
                              <Form.Label>Section</Form.Label>
                              <Form.Control as="select" name="section">
                                <option>Breakfast</option>
                                <option>Lunch</option>
                                <option>Appetizers</option>
                              </Form.Control>
                            </Form.Group>
                          </Col>
                        </Row>
                        <Button>ADD</Button>
                        <Button>Cancel</Button>
                      </Form>
                    </div>
                  ) : (
                    <div></div>
                  )}
                </div>
              </Container>
            </Col>
          </Row>
        </div>
      </div>
    );
  }
}
function mapStoreToProps(state) {
  return {
    state
  };
}
export default connect(mapStoreToProps)(Menu);
