import React, { Component } from "react";
import { connect } from "react-redux";
import * as actionCreators from "../../js/actions/ownerActions";
import { Row, Col } from "react-bootstrap";

class Cart extends Component {
  constructor(props) {
    super();
    this.state = {};
  }

  componentWillMount() {
    this.props.getCartItems(this.props.user.id);
  }
  componentWillReceiveProps(nextProps) {
    const newUser = nextProps.user;
    if (newUser !== this.props.user) {
      console.log(newUser);
      this.props.getCartItems(newUser.id);
    }
  }
  render() {
    // let cartItemsDisplay = this.props.cartItems.map(item => {
    //   return (
    //     <div>
    //       <Row>
    //         <Col>{item.quantity}</Col>
    //         <Col>{item.name}</Col>
    //         <Col>
    //           <i class="trash icon"></i>
    //         </Col>
    //         <Col>{item.price}</Col>
    //       </Row>
    //       <hr></hr>
    //     </div>
    //   );
    // });
    return (
      <div>
        <hr></hr>
        {this.props.cartItems.length > 0 ? (
          //{ cartItemsDisplay }
          <div>Items</div>
        ) : (
          <div>Your Cart is Empty</div>
        )}
      </div>
    );
  }
}
function mapStoreToProps(state) {
  return {
    user: state.userDetails.userProfile,
    cartItems: state.ownerDetails.cartItems
  };
}
export default connect(
  mapStoreToProps,
  actionCreators
)(Cart);
