import React, { Component } from "react";
import Navigation from "../Navbar";
import { connect } from "react-redux";
import * as actionCreators from "../../js/actions/index";
import { Row, Col, Container, Button } from "react-bootstrap";
import "../../css/sidebar.css";
import Form from "react-bootstrap/Form";

class UserProfile extends Component {
  constructor(props) {
    super();
    this.state = {
      isNameEditable: false
    };
  }
  componentWillMount() {
    this.props.getUserProfile(localStorage.getItem("email"));
    console.log(this.props.profile);
  }
  handleNameEdit = event => {
    this.setState({ isNameEditable: true });
  };
  render() {
    let nameEdit = null;
    if (this.state.isEditable) {
      nameEdit = (
        <div>
          <h3>Edit Name</h3>
          <Form className="col-sm-5">
            <Form.Group>
              <Form.Label>First Name</Form.Label>
              <Form.Control name="firstName"></Form.Control>
            </Form.Group>
            <Form.Group>
              <Form.Label>Last Name</Form.Label>
              <Form.Control name="lastName"></Form.Control>
            </Form.Group>
            <Form.Group>
              <Button>Update Name</Button>
              <Button variant="outline-secondary">Cancel</Button>
            </Form.Group>
          </Form>
        </div>
      );
    } else {
      nameEdit = (
        <div>
          <label className="pt-2">
            <b>Name</b>
          </label>
          <div>
            kavyashree chandrashekar
            <div class="float-right" href="#" onClick={this.handleNameEdit}>
              Edit
            </div>
          </div>
          <hr></hr>
        </div>
      );
    }
    return (
      <div>
        <Navigation></Navigation>
        <div>
          <Row>
            <Col className="col-sm-3">
              <div className="vertical-menu bg-light">
                <a href="#" className="header">
                  <h2>Your Account</h2>
                </a>
                <a href="#" className="active">
                  Profile
                </a>
                <a href="#">Address and phone</a>
                <a href="#">Past orders</a>
                <a href="#">Upcoming orders</a>
              </div>
            </Col>
            <Col className="col-sm-9">
              <Container>
                <h3 className="pt-5">Your Account</h3>

                {nameEdit}
              </Container>
            </Col>
          </Row>
        </div>
      </div>
    );
  }
}

function mapStoreToProps(state) {
  return {
    profile: state.userDetails
  };
}
export default connect(
  mapStoreToProps,
  actionCreators
)(UserProfile);
