import React, { Component } from "react";
import Navigation from "../Navbar";
import cookie from "react-cookies";
import { connect } from "react-redux";
import * as actionCreators from "../../js/actions/ownerActions";
import {
  Container,
  Card,
  Col,
  Row,
  Modal,
  Button,
  Image,
  InputGroup,
  FormControl
} from "react-bootstrap";
import image from "../../images/pizza.jpeg";
import "../../css/grubhub.css";

class DetailsPage extends Component {
  constructor(props) {
    super();
    this.state = {
      itemModalShow: false,
      item: {},
      count: 1
    };
  }
  componentWillMount() {
    if (!cookie.load("cookie")) {
      localStorage.removeItem("email");
      localStorage.removeItem("password");
      this.props.history.push("/SignIn");
    }
    this.props.getMenu(this.props.location.state.id);
    this.props.getRestaurantDetailsById(this.props.location.state.id);
  }
  handleCardClick = id => event => {
    const item = this.props.menus.find(menu => menu.id === id);
    this.setState({
      itemModalShow: true,
      item: item
    });

    console.log(item);
  };
  handleItemModalClose = () => {
    this.setState({
      itemModalShow: false,
      item: {},
      count: 1
    });
  };
  increment = () => {
    this.setState({
      count: parseInt(this.state.count) + 1
    });
  };
  decrement = () => {
    this.setState({
      count: parseInt(this.state.count) - 1
    });
  };
  handleChange = event => {
    this.setState({
      count: event.target.value
    });
  };
  render() {
    let breakfastList = this.props.menus.map(menu => {
      if (menu.section === "Breakfast") {
        return (
          <Card
            as={Col}
            md={5}
            style={{
              margin: " 8px 0 0 8px"
            }}
          >
            <a onClick={this.handleCardClick(menu.id)}>
              <Card.Img variant="top" src={image} />
              <Card.Body>
                <Card.Title>{menu.name}</Card.Title>
                <Card.Subtitle className="mb-2 text-muted">
                  {menu.description}
                </Card.Subtitle>
                <Card.Text>{menu.price}$</Card.Text>
              </Card.Body>
            </a>
          </Card>
        );
      }
      return null;
    });
    let lunchList = this.props.menus.map(menu => {
      if (menu.section === "Lunch") {
        return (
          <Card
            as={Col}
            md={5}
            style={{
              margin: " 8px 0 0 8px"
            }}
          >
            <a>
              <Card.Img variant="top" src={image} />
              <Card.Body>
                <Card.Title>{menu.name}</Card.Title>
                <Card.Subtitle className="mb-2 text-muted">
                  {menu.description}
                </Card.Subtitle>
                <Card.Text>{menu.price}$</Card.Text>
              </Card.Body>
            </a>
          </Card>
        );
      }
      return null;
    });
    let appetizersList = this.props.menus.map(menu => {
      if (menu.section === "Appetizers") {
        return (
          <Card
            as={Col}
            md={5}
            style={{
              margin: " 8px 0 0 8px"
            }}
          >
            <a>
              <Card.Img variant="top" src={image} />
              <Card.Body>
                <Card.Title>{menu.name}</Card.Title>
                <Card.Subtitle className="mb-2 text-muted">
                  {menu.description}
                </Card.Subtitle>
                <Card.Text>{menu.price}$</Card.Text>
              </Card.Body>
            </a>
          </Card>
        );
      }
      return null;
    });
    return (
      <div>
        <Navigation></Navigation>
        <Container>
          <div
            style={{
              height: "170px !important",
              display: "block"
            }}
          >
            <img src={image}></img>
          </div>
          <h2>{this.props.restaurant.name || ""}</h2>
          <hr></hr>
          <h4>Menu</h4>
          <hr></hr>
          <h3>Breakfast Menu</h3>
          <Row>{breakfastList}</Row>
          <hr></hr>
          <h3>Lunch Menu</h3>
          <Row>{lunchList}</Row>
          <hr></hr>
          <h3>Appetizers Menu</h3>
          <Row>{appetizersList}</Row>
          <Modal
            show={this.state.itemModalShow}
            onHide={this.handleItemModalClose}
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            centered
          >
            <Modal.Header closeButton>
              <Modal.Title>Add Item to Cart</Modal.Title>
            </Modal.Header>
            <Row>
              <Col>
                <Modal.Body>
                  <h3 className="text-center">{this.state.item.name}</h3>
                  <p className="text-center">{this.state.item.price} $</p>
                  <div className="d-flex justify-content-center">
                    <InputGroup className="col-sm-5">
                      <InputGroup.Prepend>
                        <Button
                          variant="outline-secondary"
                          onClick={this.decrement}
                          style={{
                            margin: "0 0 0 0"
                          }}
                        >
                          -
                        </Button>
                      </InputGroup.Prepend>
                      <FormControl
                        aria-label="Large"
                        value={this.state.count}
                        name="cuisine"
                        aria-describedby="inputGroup-sizing-sm"
                        onChange={this.handleChange}
                        style={{
                          MozAppearance: "textfield"
                        }}
                        type="number"
                        min="1"
                        max="1000"
                      ></FormControl>

                      <InputGroup.Append>
                        <Button
                          variant="outline-secondary"
                          onClick={this.increment}
                        >
                          +
                        </Button>
                      </InputGroup.Append>
                    </InputGroup>
                  </div>
                </Modal.Body>
              </Col>
              <Col>
                <Image src={image} thumbnail></Image>
              </Col>
            </Row>
            <Modal.Footer>
              <Button variant="secondary" onClick={this.handleItemModalClose}>
                Close
              </Button>
              <Button onClick={this.addToCart} variant="primary">
                Add to Cart : {this.state.count * this.state.item.price} $
              </Button>
            </Modal.Footer>
          </Modal>
        </Container>
      </div>
    );
  }
}

function mapStoreToProps(state) {
  return {
    menus: state.ownerDetails.menu,
    restaurant: state.ownerDetails.restaurantDetailsID
  };
}
export default connect(
  mapStoreToProps,
  actionCreators
)(DetailsPage);
