import React, { Component } from "react";
import Navigation from "../Navbar";
import { connect } from "react-redux";
import { Row, Col, Container, Button, Table } from "react-bootstrap";
import "../../css/sidebar.css";
import Form from "react-bootstrap/Form";
import cookie from "react-cookies";
import * as actionCreators from "../../js/actions/ownerActions";

class Menu extends Component {
  constructor(props) {
    super();
    this.state = {
      isAddItem: false
    };
  }
  async componentWillMount() {
    if (!cookie.load("cookie")) {
      localStorage.removeItem("email");
      localStorage.removeItem("password");
      this.props.history.push("/SignIn");
    }
    await this.props.getRestaurantDetails(localStorage.getItem("email"));
    console.log(this.props.restaurant);
    if (this.props.restaurant[0]) {
      await this.props.getMenu(this.props.restaurant[0].id);
    }
  }
  addItem = () => {
    this.setState({
      isAddItem: true
    });
  };
  render() {
    return (
      <div>
        <Navigation></Navigation>
        <div>
          <Row>
            <Col className="col-sm-3">
              <div className="vertical-menu bg-light">
                <a href="#" className="header">
                  <h2>Your Account</h2>
                </a>
                <a href="#">Home</a>
                <a href="#" className="active">
                  Menu
                </a>
                <a href="#">Profile</a>
                <a href="#">Past Orders</a>
              </div>
            </Col>
            <Col className="col-sm-9">
              <Container>
                <h3 className="pt-5">Manage Restaurant Menu</h3>
                <Button onClick={this.addItem}>Add Item</Button>
                <div>
                  {this.state.isAddItem ? (
                    <div>
                      <Form>
                        <Row>
                          <Col>
                            <Form.Group>
                              <Form.Label>Item Name</Form.Label>
                              <Form.Control name="itemName"></Form.Control>
                            </Form.Group>
                          </Col>
                          <Col>
                            <Form.Group>
                              <Form.Label>Item Description</Form.Label>
                              <Form.Control name="desc"></Form.Control>
                            </Form.Group>
                          </Col>

                          <Col>
                            <Form.Group>
                              <Form.Label>Price</Form.Label>
                              <Form.Control name="price"></Form.Control>
                            </Form.Group>
                          </Col>
                          <Col>
                            <Form.Group>
                              <Form.Label>Section</Form.Label>
                              <Form.Control as="select" name="section">
                                <option>Breakfast</option>
                                <option>Lunch</option>
                                <option>Appetizers</option>
                              </Form.Control>
                            </Form.Group>
                          </Col>
                        </Row>
                        <Button>ADD</Button>
                        <Button variant="outline-secondary">Cancel</Button>
                      </Form>
                    </div>
                  ) : (
                    <div></div>
                  )}
                  <Table striped bordered hover>
                    <thead>
                      <tr>
                        <th>Name</th>
                        <th>Description</th>
                        <th>Image</th>
                        <th>Price</th>
                        <th>Section</th>
                      </tr>
                    </thead>
                  </Table>
                </div>
              </Container>
            </Col>
          </Row>
        </div>
      </div>
    );
  }
}
function mapStoreToProps(state) {
  return {
    restaurant: state.ownerDetails.restaurantDetails
  };
}
export default connect(
  mapStoreToProps,
  actionCreators
)(Menu);
