import React, { Component } from "react";
import Navigation from "../Navbar";
import { connect } from "react-redux";
import * as actionCreators from "../../js/actions/ownerActions";
import {
  Row,
  Col,
  Container,
  Button,
  Toast,
  Card,
  Modal
} from "react-bootstrap";
import "../../css/sidebar.css";
import Form from "react-bootstrap/Form";
import cookie from "react-cookies";

class OwnerHome extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [],
      showModal: false,
      orderDetails: null,
      updateStatus: false,
      status: ""
    };
  }

  componentWillMount() {
    if (!cookie.load("cookie")) {
      localStorage.removeItem("email");
      localStorage.removeItem("password");
      this.props.history.push("/SignIn");
    }
    this.props.getRestaurantDetails(localStorage.getItem("email"));
  }
  componentWillReceiveProps(nextProps) {
    const newRestaurantDetails = nextProps.restaurant;
    const newOrderItems = nextProps.getItems;
    if (newRestaurantDetails !== this.props.restaurant) {
      this.props.getOrdersforRestaurant(newRestaurantDetails.id);
    }
    if (newOrderItems !== this.props.getItems) {
      console.log("here");
      this.setState({
        items: newOrderItems
      });
    }
  }

  convertTodate(date) {
    date = date.split(".")[0];
    let dateTimeParts = date.split(/[-T :]/);
    dateTimeParts[1]--;
    console.log(dateTimeParts);
    return new Date(Date.UTC(...dateTimeParts));
  }
  getOrderDetails = id => event => {
    console.log(id);
    this.props.getOrdersItems(id);
    this.setState({
      showModal: true,
      orderDetails: this.props.getOrders.find(order => order.id === id)
    });
  };

  setUpdateStatus = () => {
    this.setState({
      updateStatus: true
    });
  };
  updateItemStatus = () => {
    if (this.state.status.length !== 0) {
      let data = {
        id: this.state.orderDetails.id,
        status: this.state.status
      };
      console.log(this.state.status);
      this.props.updateItemStatus(data);
    }
    this.setState({
      updateStatus: false
    });
    this.setState(prevState => ({
      orderDetails: {
        ...prevState.orderDetails,
        status: this.state.status
      }
    }));
  };
  handleUpdateChange = event => {
    this.setState({
      status: event.target.value
    });
  };
  render() {
    let newOrderDetails = this.props.getOrders.map(order => {
      if (order.status === "New") {
        return (
          <div
            as={Col}
            md={3}
            style={{
              margin: " 8px 0 0 8px",
              cursor: "pointer"
            }}
          >
            <Card onClick={this.getOrderDetails(order.id)}>
              <Card.Header>Order Id:{order.id}</Card.Header>
              <Card.Body>
                <Card.Title>New</Card.Title>
                <Card.Text>
                  Time:
                  {
                    order.createdAt
                      .toString()
                      .slice(0, 19)
                      .replace("T", " ")
                      .split(" ")[1]
                  }
                </Card.Text>
                <Card.Text>
                  Date:
                  {
                    order.createdAt
                      .toString()
                      .slice(0, 19)
                      .replace("T", " ")
                      .split(" ")[0]
                  }
                </Card.Text>
                <Card.Text>
                  {order.firstName} {order.lastName}
                </Card.Text>
              </Card.Body>
            </Card>
          </div>
        );
      }
    });
    let itemDetails = this.state.items.map(item => {
      return (
        <div>
          {item.itemName}({item.quantity}): {item.price}$
        </div>
      );
    });
    return (
      <div>
        <Navigation></Navigation>
        <Row>
          <Col className="col-sm-3">
            <div className="vertical-menu bg-light">
              <a className="header">
                <h2>Home</h2>
              </a>
              <a href="/OwnerHome" className="active">
                Home
              </a>
              <a href="/Menu">Menu</a>
              <a href="/OwnerProfile">Profile</a>
              <a href="#">Past Orders</a>
            </div>
          </Col>
          <Col className="col-sm-9">
            <Container>
              <div className="pt-3">
                <h1>Your Orders</h1>
              </div>
              <hr></hr>
              <div>
                <h3>New Orders</h3>
              </div>
              <Row>{newOrderDetails}</Row>
              <Modal
                show={this.state.showModal}
                onHide={this.handleClose}
                size="lg"
                aria-labelledby="contained-modal-title-vcenter"
                centered
              >
                <Modal.Header closeButton>
                  <Modal.Title>Order Details</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                  {this.state.items.length > 0 ? (
                    <div>
                      <Row>
                        <Col md={4}>
                          <div>
                            <b>Customer Details: </b>
                          </div>
                          <div>
                            {this.state.orderDetails.firstName}{" "}
                            {this.state.orderDetails.lastName}
                          </div>
                          <div>{this.state.orderDetails.phoneNO}</div>
                          <div>{this.state.orderDetails.address}</div>
                        </Col>
                        <Col md={4}>
                          <b>Items:</b>
                          {itemDetails}
                        </Col>
                        <Col md={4}>
                          <b>Status:</b>
                          <div>{this.state.orderDetails.status}</div>
                        </Col>
                      </Row>
                      <Row>
                        {this.state.updateStatus ? (
                          <Col md={4}>
                            <Form>
                              <Form.Group>
                                <Form.Control
                                  as="select"
                                  onChange={this.handleUpdateChange}
                                >
                                  <option>New</option>
                                  <option>Preparing</option>
                                  <option>Ready</option>
                                  <option>Delivered</option>
                                </Form.Control>
                              </Form.Group>
                            </Form>
                            <Button onClick={this.updateItemStatus}>
                              Update
                            </Button>
                          </Col>
                        ) : (
                          <Col>
                            <Button
                              variant="secondary"
                              onClick={this.setUpdateStatus}
                            >
                              Update Status
                            </Button>
                          </Col>
                        )}
                      </Row>
                    </div>
                  ) : (
                    <div></div>
                  )}
                </Modal.Body>
              </Modal>
            </Container>
          </Col>
        </Row>
      </div>
    );
  }
}
function mapStoreToProps(state) {
  return {
    getOrders: state.ownerDetails.getOrdersRestaurant,
    restaurant: state.ownerDetails.restaurantDetails,
    getItems: state.ownerDetails.getOrderItems
  };
}
export default connect(
  mapStoreToProps,
  actionCreators
)(OwnerHome);
