import React, { Component } from "react";
import { connect } from "react-redux";
import { Button, Container, Tabs, Tab, Form } from "react-bootstrap";
import "../../css/grubhub.css";

class SignIn extends Component {
  constructor(props) {
    super(props);
    this.state = {
      activeTab: props.activeTab || 1,
      name: "",
      email: "",
      password: ""
    };
  }

  handleSelect = selectedTab => {
    this.setState({
      activeTab: selectedTab
    });
  };

  handleUserLogin = () => {};
  nameRequired = "";
  emailRequired = "";
  passwordRequired = "";
  validateUser = () => {
    flag = true;
    if (this.state.name.trim().length === 0) {
      nameRequired = (
        <div style={{ fontSize: 12, color: "red" }}>*Required</div>
      );
      flag = false;
    }
    if (this.state.email.trim().length === 0) {
      emailRequired = (
        <div style={{ fontSize: 12, color: "red" }}>*Required</div>
      );
      flag = false;
    }
    if (this.state.password.length === 0) {
      passwordRequired = (
        <div style={{ fontSize: 12, color: "red" }}>*Required</div>
      );
      flag = false;
    }
    return flag;
  };
  render() {
    return (
      <div>
        <Container>
          <div className="login u-dimension-1">
            <Tabs activeKey={this.state.activeTab} onSelect={this.handleSelect}>
              <Tab eventKey={1} title="Sign In as Customer">
                <h3 className="pt-5">Sign In with your GrubHub Account</h3>
                <Form className="pt-2">
                  <Form.Group>
                    <Form.Label>Name</Form.Label>
                    <Form.Control name="name"></Form.Control>
                  </Form.Group>
                  <Form.Group>
                    <Form.Label>Email</Form.Label>
                    <Form.Control name="Email"></Form.Control>
                  </Form.Group>
                  <Form.Group>
                    <Form.Label>Password</Form.Label>
                    <Form.Control
                      name="Password"
                      type="password"
                    ></Form.Control>
                  </Form.Group>
                  <Form.Group>
                    <Button
                      variant="primary"
                      type="submit"
                      className="btn-block"
                    >
                      Sign In
                    </Button>
                  </Form.Group>
                </Form>
                <div className="text-center">
                  <a href="/UserLogin">Create your Account</a>
                </div>
              </Tab>
              <Tab eventKey={2} title="Sign In as Owner">
                <h3 className="pt-5">Sign In with your GrubHub Account</h3>
                <Form className="pt-2">
                  <Form.Group>
                    <Form.Label>Name</Form.Label>
                    <Form.Control name="name"></Form.Control>
                  </Form.Group>
                  <Form.Group>
                    <Form.Label>Email</Form.Label>
                    <Form.Control name="Email"></Form.Control>
                  </Form.Group>
                  <Form.Group>
                    <Form.Label>Password</Form.Label>
                    <Form.Control
                      name="Password"
                      type="password"
                    ></Form.Control>
                  </Form.Group>
                  <Form.Group>
                    <Button
                      variant="primary"
                      type="submit"
                      className="btn-block"
                    >
                      Sign In
                    </Button>
                  </Form.Group>
                </Form>
                <div className="text-center">
                  <a href="/OwnerLogin">Create your Account</a>
                </div>
              </Tab>
            </Tabs>
          </div>
        </Container>
      </div>
    );
  }
}
function mapStoreToProps(state) {
  return state;
}
export default connect(mapStoreToProps)(SignIn);
