import React, { Component } from "react";
import { connect } from "react-redux";
import * as actionCreators from "../../js/actions/ownerActions";
import { Row, Col, Button } from "react-bootstrap";

class Cart extends Component {
  constructor(props) {
    super();
    this.state = {
      items: [],
      sum: 0.0
    };
  }

  componentWillMount() {
    this.props.getCartItems(this.props.user.id);
  }
  componentWillReceiveProps(nextProps) {
    const newUser = nextProps.user;
    const newcartItems = nextProps.cartItems;
    const newOrderPlaced = nextProps.orderPlaced;
    if (newUser !== this.props.user) {
      console.log(newUser);
      this.props.getCartItems(newUser.id);
    }
    if (newcartItems !== this.props.cartItems) {
      let total = 0.0;
      newcartItems.map(item => {
        total = total + parseFloat(item.price);
      });
      this.setState({
        items: newcartItems,
        sum: total
      });
    }
    if (
      newOrderPlaced !== this.props.orderPlaced &&
      newOrderPlaced === "Successfully order placed"
    ) {
      this.props.deleteAllCartItems(this.props.user.id);
    }
  }
  deleteItem = id => event => {
    console.log(id);
    this.props.deleteCartItem(id, this.props.user.id);
  };
  placeOrder = () => {
    let data = {
      items: this.state.items,
      userId: this.props.user.id,
      restaurantId: this.props.restaurant.id
    };
    this.props.placeOrder(data);
  };
  render() {
    let cartItemsDisplay = this.state.items.map(item => {
      return (
        <div>
          <Row>
            <Col md={1}>{item.quantity}</Col>
            <Col
              md={3}
              style={{
                color: "blue"
              }}
            >
              {item.itemName}
            </Col>
            <Col
              md={1}
              style={{
                cursor: "pointer"
              }}
              onClick={this.deleteItem(item.id)}
            >
              <i class="trash icon"></i>
            </Col>
            <Col>${item.price}</Col>
          </Row>
          <hr></hr>
        </div>
      );
    });
    return (
      <div>
        <hr></hr>
        {cartItemsDisplay}
        <div>
          {this.state.items.length === 0 ? (
            <a>Your cart is empty</a>
          ) : (
            <div>
              <a>Items Total: $ {this.state.sum}</a>
              <Button
                variant="success"
                className="btn-block"
                onClick={this.placeOrder}
              >
                Place Order
              </Button>
            </div>
          )}
        </div>
      </div>
    );
  }
}
function mapStoreToProps(state) {
  return {
    user: state.userDetails.userProfile,
    cartItems: state.ownerDetails.cartItems,
    restaurant: state.ownerDetails.restaurantDetailsID,
    orderPlaced: state.ownerDetails.orderPlaced
  };
}
export default connect(
  mapStoreToProps,
  actionCreators
)(Cart);
